  const [taskInput, setTaskInput] = useState("");
  const [taskList, setTaskList] = useState([
    {
      Task: "Task One",
      isComplete: false,
    },
    {
      Task: "Task two",
      isComplete: true,
    },
    {
      Task: "Task three",
      isComplete: false,
    },
  ]);

  const addTasks = () => {
    if (taskInput === "") {
      alert("Plz add Task First");
