  //             return index !== id;
  //         })
  //      })
  // }

  return (
    <>
      <div className="main_container">
        <Header />
        <Input
          inputTask={taskInput}
          setTask={setTaskInput}
          addTaskList={addTasks}
        />
        <Task taskList={taskList} setTaskList={setTaskList} />
      </div>
    </>
  );
};

export default App;
