import React, {useState } from 'react'
import './style.css';

const App = () => {

    const [taskInput, setTaskInput] = useState('');

    const [taskList, setTaskList] = useState([])
    
    const addTasks = () =>{
        setTaskList((oldData) => {
            return [...oldData , taskInput]
        });
    }

    console.log(taskList);
    

  return (
    <>
        <h1>My TODO List</h1>
        <div className='TodoMain'>
            <div className='inputDiv'>
                <input type='text' placeholder='Task Here ...' onChange={(e) => {setTaskInput(e.target.value)}} value={taskInput}/>
                <button onClick={addTasks}>Add Task</button>
            </div>
            <div className='TaskListMain'>
                <ul>
                    {taskList?.map((value) => 
                        <li>{value}</li>)}
                </ul>
            </div>
        </div>
    </>
  )
}

export default App
