import React from "react";

const Task = ({ taskList, deleteItem,  setTaskList }) => {

  return (
    <>
                {taskList?.map((task , index) => {
                  return (
                    <li>
                      <div className="row no-gutters align-self-center">
                        <div className="col-10">
                          <p>
                            {task.isComplete ? <s>{task.Task}</s> : task.Task}
                          </p>
                        </div>
                        <div className="col-1">
                          <button
                            disabled={task.isComplete}
                            className="btn btn-success"
                          >
                            <i className="fa-solid fa-check"></i>
                          </button>
                        </div>
                        <div className="col-1">
                          <button className="btn btn-danger" type="button" onClick={console.log('Testing')}>
                            <i className="fa-solid fa-trash"></i>
                          </button>
                        </div>
                      </div>
                    </li>
                  );
                })}
            
    </>
  );
};

export default Task;
