import React, {useState } from 'react'
import './style.css';
import Task from './components/Task';

const App = () => {

    const [taskInput, setTaskInput] = useState('');
    const [taskList, setTaskList] = useState(['Task One', 'Task Two', 'Task three'])
    
    const addTasks = () =>{
        console.log(taskInput);
        setTaskList((oldData) => {
            return [...oldData , taskInput];
        });
        setTaskInput('');
    }   
    
    // const deleteItem = (id) =>{
    //     const tempArry = [...taskList];
    //     tempArry.splice(id , 1);
    //     setTaskList(tempArry);
    // }


    const deleteItem = (id) =>{
        
        setTaskList((oldData) => { 
            return oldData.filter((data, index) => {
                return index !== id;
            })
         })
    }

  return (
    <>
        <h1>My TODO List</h1>
        <div className='TodoMain'>
            <div className='inputDiv'>
                <input type='text' placeholder='Task Here ...' onChange={(e) => {setTaskInput(e.target.value)}} value={taskInput}/>
                <button onClick={addTasks}>+</button>
            </div>
            <div className='TaskListMain'>
                <ul>
                    {taskList?.map((value , index) => 
                        <Task takList={value} key={index} id={index} deleteItem={deleteItem}/>)}
                </ul>
            </div>
        </div>
    </>
  )
}

export default App
