import React from "react";

const Task = ({ takList, deleteItem, id }) => {
  return (
    <>
      <li>
        {takList}
        <div className="button">
        <button
          onClick={() => {
            deleteItem(id);
          }}
        >
          <i class="fa-solid fa-check"></i>
        </button>
        <button
          onClick={() => {
            deleteItem(id);
          }}
        >
          <i class="fa-solid fa-xmark"></i>
        </button>
        </div>
      </li>
    </>
  );
};

export default Task;
