  return (
    <>
      <div className="main_container">
        <section className="TodoList_header">
          <div className="container">
            <div className="row">
              <div className="col-md-12 text-center">
                <h1>TODO LIST</h1>
              </div>
            </div>
          </div>
        </section>
        
        
      </div>
    </>
  );
};

export default App;
