import React, { useState } from "react";
import "./style.css";
import Task from "./components/Task";

const App = () => {
  const [taskInput, setTaskInput] = useState("");
  const [taskList, setTaskList] = useState([]);

  const addTasks = () => {
    console.log(taskInput);
    if (taskInput === "") {
      alert("Plz add Task First");
    } else {
      setTaskList((oldData) => {
        return [...oldData, taskInput];
      });
    }
    setTaskInput("");
  };

  const deleteItem = (id) => {
    const tempArry = [...taskList];
    tempArry.splice(id, 1);
    setTaskList(tempArry);
  };

  // const deleteItem = (id) =>{

  //     setTaskList((oldData) => {
  //         return oldData.filter((data, index) => {
  //             return index !== id;
  //         })
  //      })
  // }

  return (
    <>
      <div className="main_container">
        <section className="TodoList_header">
          <div className="container">
            <div className="row">
              <div className="col-md-12 text-center">
                <h1>TODO LIST</h1>
              </div>
            </div>
          </div>
        </section>
        <section className="TodoList_input">
          <div className="container">
            <div className="row">
              <div className="col-md-12">
                <div class="input-group">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="ADD TASK ..."
                  />
                  <div class="input-group-append">
                    <button class="btn btn-success" type="button">
                      <i class="fa-solid fa-plus"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
        
      </div>
    </>
  );
};

export default App;
